mapper.select:
SELECT * FROM car c
AND car_number = #{filter.carNumber}
AND status = #{filter.status}
AND brand LIKE CONCAT('%', #{filter.brand}, '%')
AND model LIKE CONCAT('%', #{filter.model}, '%')
AND (brand LIKE CONCAT('%', #{filter.brand}, '%') OR model LIKE CONCAT('%', #{filter.brand}, '%') OR CONCAT(brand, model) LIKE CONCAT('%', #{filter.brand}, '%'))
ORDER BY car_number ASC LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(*) FROM car
AND car_number = #{filter.carNumber}
AND status = #{filter.status}
AND brand LIKE CONCAT('%', #{filter.brand}, '%')
AND model LIKE CONCAT('%', #{filter.model}, '%')
AND (brand LIKE CONCAT('%', #{filter.brand}, '%') OR model LIKE CONCAT('%', #{filter.brand}, '%') OR CONCAT(brand, model) LIKE CONCAT('%', #{filter.brand}, '%'))
