mapper.select:
SELECT c.car_number, c.brand, c.model, c.status, dl.start_time, dl.end_time, dl.start_point
       ,dl.end_point, dl.drive_dist, dl.memo FROM drive_log dl JOIN car c ON dl.car_id = c.car_id


            AND c.car_number = #{filter.carNumber}


            AND c.status = #{filter.status}


            AND c.brand = #{filter.brand}


            AND c.model = #{filter.model}


            AND (c.brand = #{filter.brand} OR c.model = #{filter.brand})


            AND dl.start_time >= CONCAT(#{filter.startTime}, ' 00:00:00')
            AND dl.start_time <  DATE_ADD(#{filter.endTime}, INTERVAL 1 DAY)


ORDER BY

        c.car_number
        start_time
        end_time


    ASC
    DESC

    LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(*) FROM drive_log dl JOIN car c ON dl.car_id = c.car_id


        AND c.car_number = #{filter.carNumber}


        AND c.status = #{filter.status}


        AND c.brand = #{filter.brand}


        AND c.model = #{filter.model}


        AND (c.brand = #{filter.brand} OR c.model = #{filter.brand})


        AND dl.start_time >= CONCAT(#{filter.startTime}, ' 00:00:00')
        AND dl.start_time <  DATE_ADD(#{filter.endTime}, INTERVAL 1 DAY)
